target.groovy:

import org.acegisecurity.providers.dao.SaltSource;
import org.acegisecurity.providers.dao.salt.SystemWideSaltSource;
import org.acegisecurity.providers.encoding.Md5PasswordEncoder;
import org.acegisecurity.providers.encoding.PasswordEncoder;

String systemWideSalt = properties["salt"];
SaltSource ss = new SystemWideSaltSource();
ss.setSystemWideSalt(systemWideSalt);

PasswordEncoder pe = new Md5PasswordEncoder();
pe.setEncodeHashAsBase64(true);

String plainTextPassword = properties["password"];
Object salt = ss.getSalt(null);
String password = pe.encodePassword(plainTextPassword, salt);
ant.echo("Encoded password is ${password}");
